    const expectedBody = pm.environment.get("savedResponseBody");

    if (!expectedBody || expectedBody.trim() === "") {
        console.log("Skipping response body comparison: savedResponseBody is not set or is empty.");
        return;
    }

    try {
        const actualBody = pm.response.json();
        const expectedJson = JSON.parse(expectedBody);
        pm.expect(actualBody).to.deep.equal(expectedJson);
    } catch (e) {
        pm.test("Failed to parse or compare JSON bodies", function() {
            throw e;
        });
    }
});

